DXGI_SWAP_CHAIN_DESC1 swapChainDesc = {0};

       swapChainDesc.Width = lround(m_d3dRenderTargetSize.Width);    // Match the size of the window.
       swapChainDesc.Height = lround(m_d3dRenderTargetSize.Height);
       swapChainDesc.Format = DXGI_FORMAT_B8G8R8A8_UNORM;            // This is the most common swap chain format.
       swapChainDesc.Stereo = false;
       swapChainDesc.SampleDesc.Count = 1;                           // Don't use multi-sampling.
       swapChainDesc.SampleDesc.Quality = 0;
       swapChainDesc.BufferUsage = DXGI_USAGE_RENDER_TARGET_OUTPUT;
       swapChainDesc.BufferCount = 2;                                // Use double-buffering to minimize latency.
       swapChainDesc.SwapEffect = DXGI_SWAP_EFFECT_FLIP_DISCARD;     // All Windows Store apps must use a flip effect.
       swapChainDesc.Flags = 2048;
       swapChainDesc.Scaling = scaling;
       swapChainDesc.AlphaMode = DXGI_ALPHA_MODE_IGNORE;

       // This sequence obtains the DXGI factory that was used to create the Direct3D device above.
       ComPtr<IDXGIDevice3> dxgiDevice;
       DX.ThrowIfFailed(m_d3dDevice.As(&amp;amp;amp;amp;amp;amp;dxgiDevice));

       ComPtr<IDXGIAdapter> dxgiAdapter;
       DX.ThrowIfFailed(dxgiDevice->GetAdapter(&amp;amp;amp;amp;amp;amp;dxgiAdapter));

       ComPtr<IDXGIFactory4> dxgiFactory;
       DX.ThrowIfFailed(dxgiAdapter->GetParent(IID_PPV_ARGS(&amp;amp;amp;amp;amp;amp;dxgiFactory)));

       ComPtr<IDXGISwapChain1> swapChain;
       DX.ThrowIfFailed(
              dxgiFactory->CreateSwapChainForCoreWindow(
                     m_d3dDevice.Get(),
                     reinterpret_cast<IUnknown*>(m_window.Get()),
                     &amp;amp;amp;amp;amp;amp;swapChainDesc,
                     nullptr,
                     &amp;amp;amp;amp;amp;amp;swapChain
                     )
              );
