remarks.code:
typedef union switch(DWORD tyspec)
    {
    case TYSPEC_CLSID:
        CLSID   clsid;
    case TYSPEC_FILEEXT:
        LPOLESTR pFileExt;
    case TYSPEC_MIMETYPE:
        LPOLESTR pMimeType;
    case TYSPEC_PROGID:
        LPOLESTR pProgId;
    case TYSPEC_FILENAME:
        LPOLESTR pFileName;
    case TYSPEC_PACKAGENAME:
        struct {
        LPOLESTR pPackageName;
        GUID     PolicyId;
        } ByName;
    case TYSPEC_OBJECTID:
        struct {
        GUID     ObjectId;
        GUID     PolicyId;
        } ByObjectId;
} uCLSSPEC;
