HRESULT UpdateDesktop(HWND hwnd)
{
    HRESULT hr = S_OK;

    // Register the thumbnail
    HTHUMBNAIL thumbnail = NULL;

    hr = DwmRegisterThumbnail(hwnd, FindWindow(_T("Progman"), NULL), &amp;amp;amp;amp;amp;thumbnail);
    if (SUCCEEDED(hr))
    {
        // Display the thumbnail using DwmUpdateThumbnailProperties
    }
    return hr;
}

// Set DWM window attributes to provide the iconic bitmap, and
// to always render the thumbnail using the iconic bitmap.
BOOL fForceIconic = TRUE;
BOOL fHasIconicBitmap = TRUE;

DwmSetWindowAttribute(
    hwnd,
    DWMWA_FORCE_ICONIC_REPRESENTATION,
    &amp;amp;amp;amp;amp;fForceIconic,
    sizeof(fForceIconic));

DwmSetWindowAttribute(
    hwnd,
    DWMWA_HAS_ICONIC_BITMAP,
    &amp;amp;amp;amp;amp;fHasIconicBitmap,
    sizeof(fHasIconicBitmap));

case WM_DWMSENDICONICLIVEPREVIEWBITMAP:
{
    // This window is being asked to provide a bitmap to show in Peek preview.
    // This indicates the thumbnail in the taskbar is being previewed.
    RECT rectWindow = {0, 0, 0, 0};
    if (GetClientRect(hwnd, &amp;amp;amp;amp;amp;rectWindow))
    {
        nWidth = rectWindow.right - rectWindow.left;
        nHeight = rectWindow.bottom - rectWindow.top;
    }

    hbm = CreateDIB(nWidth, nHeight);
    if (hbm)
    {
        hr = DwmSetIconicLivePreviewBitmap(hwnd, hbm, NULL, DWM_SIT_DISPLAYFRAME);
        DeleteObject(hbm);
    }
}
break;

// Set DWM window attributes to provide the iconic bitmap, and
// to always render the thumbnail using the iconic bitmap.
BOOL fForceIconic = TRUE;
BOOL fHasIconicBitmap = TRUE;

DwmSetWindowAttribute(
    hwnd,
    DWMWA_FORCE_ICONIC_REPRESENTATION,
    &amp;amp;amp;amp;amp;fForceIconic,
    sizeof(fForceIconic));

DwmSetWindowAttribute(
    hwnd,
    DWMWA_HAS_ICONIC_BITMAP,
    &amp;amp;amp;amp;amp;fHasIconicBitmap,
    sizeof(fHasIconicBitmap));

case WM_DWMSENDICONICTHUMBNAIL:
{
    // This window is being asked to provide its iconic bitmap. This indicates
    // a thumbnail is being drawn.
    hbm = CreateDIB(HIWORD(lParam), LOWORD(lParam));
    if (hbm)
    {
        hr = DwmSetIconicThumbnail(hwnd, hbm, 0);
        DeleteObject(hbm);
    }
}
break;

HRESULT UpdateDesktop(HWND hwnd)
{
    HRESULT hr = S_OK;

    // Register the thumbnail
    SIZE size = {100,100};
    HTHUMBNAIL thumbnail = NULL;

    hr = DwmRegisterThumbnail(hwnd, FindWindow(_T("Progman"), NULL), &amp;amp;amp;amp;amp;size, &amp;amp;amp;amp;amp;thumbnail);
    if (SUCCEEDED(hr))
    {
        // The destination rectangle size
        RECT dest = {0,50,100,150};

        // Set the thumbnail properties for use
        DWM_THUMBNAIL_PROPERTIES dskThumbProps;
        dskThumbProps.dwFlags = DWM_TNP_RECTDESTINATION | DWM_TNP_VISIBLE | DWM_TNP_SOURCECLIENTAREAONLY;

        // Use the window frame and client area
        dskThumbProps.fSourceClientAreaOnly = FALSE;
        dskThumbProps.fVisible = TRUE;
        dskThumbProps.opacity = (255 * 70)/100;
        dskThumbProps.rcDestination = dest;

        // Display the thumbnail
        hr = DwmUpdateThumbnailProperties(thumbnail,&amp;amp;amp;amp;amp;dskThumbProps);
        if (SUCCEEDED(hr))
        {
            // ...
        }
    }
    return hr;
}
