remarks.code:
IWICFastMetadataEncoder *pFME = NULL;
IWICMetadataQueryWriter *pFMEQW = NULL;

hr = pFactory->CreateFastMetadataEncoderFromFrameDecode(pFrameDecode, &amp;amp;amp;amp;amp;pFME);

if (SUCCEEDED(hr))
{
     hr = pFME->GetMetadataQueryWriter(&amp;amp;amp;amp;amp;pFMEQW);
}

if (SUCCEEDED(hr))
{
     // Add additional metadata
     PROPVARIANT value;

     PropVariantInit(&amp;amp;amp;amp;amp;value);

     value.vt = VT_UI2;
     value.uiVal = 99;
     hr = pFMEQW->SetMetadataByName(L"/app1/ifd/{ushort=18249}", &amp;amp;amp;amp;amp;value);

     PropVariantClear(&amp;amp;amp;amp;amp;value);
}

if (SUCCEEDED(hr))
{
     hr = pFME->Commit();
}
