remarks.code:
// Get the query reader
if (SUCCEEDED(hr))
{
    hr = pFrameDecode->GetMetadataQueryReader(&amp;amp;amp;amp;amp;pQueryReader);
}

if (SUCCEEDED(hr))
{
    hr = pQueryReader->GetMetadataByName(L"/app1/ifd/{ushort=18249}", &amp;amp;amp;amp;amp;value);
    PropVariantClear(&amp;amp;amp;amp;amp;value);
}

// Get the query reader
if (SUCCEEDED(hr))
{
    hr = pFrameDecode->GetMetadataQueryReader(&amp;amp;amp;amp;amp;pQueryReader);
}

if (SUCCEEDED(hr))
{
    // Get the embedded IFD reader
    hr = pQueryReader->GetMetadataByName(L"/app1/ifd", &amp;amp;amp;amp;amp;value);
    if (value.vt == VT_UNKNOWN)
    {
        hr = value.punkVal->QueryInterface(IID_IWICMetadataQueryReader, (void **)&amp;amp;amp;amp;amp;pEmbedReader);
    }
    PropVariantClear(&amp;amp;amp;amp;amp;value); // Clear value for new query
}
