remarks.code:
// Create XMP block
IWICMetadataQueryWriter *pXMPWriter = NULL;

if (SUCCEEDED(hr))
{
    hr = pFactory->CreateQueryWriter(GUID_MetadataFormatXMP, NULL, &amp;amp;amp;amp;amp;pXMPWriter);
}

// Write metadata to the XMP writer
if (SUCCEEDED(hr))
{
    PROPVARIANT value;
    PropVariantInit(&amp;amp;amp;amp;amp;value);

    value.vt = VT_LPWSTR;
    value.pwszVal = L"Metadata Test Image.";

    hr = pXMPWriter->SetMetadataByName(L"/dc:title", &amp;amp;amp;amp;amp;value);

    PropVariantClear(&amp;amp;amp;amp;amp;value);
}
